mapper.delete:
delete from HOWGETMONEY
where	member_email = #{memberEmail}
and		howgetmoney_holder = #{howgetmoneyHolder}
and		howgetmoney_bank = #{howgetmoneyBank}
and		howgetmoney_number = #{howgetmoneyNumber}

mapper.select:
select	member_email memberEmail, howgetmoney_holder howgetmoneyHolder, howgetmoney_bank howgetmoneyBank, howgetmoney_number howgetmoneyNumber
from	HOWGETMONEY
where	member_email = #{memberEmail}
