mapper.select:
select review_serial reviewSerial, member_email memberEmail, comment_content commentContent, comment_rating commentRating, comment_time commentTime
from reviewcomment
where review_serial = #{reviewSerial}
and	member_email = #{memberEmail}

mapper.update:
update REVIEWCOMMENT
set review_serial =#{reviewSerial},
	  member_email = #{memberEmail},
	  comment_content=#{commentContent},
	  comment_rating=#{commentRating},
	  comment_time=#{commentTime}
where review_serial=#{reviewSerial}
and member_email=#{memberEmail}

mapper.delete:
delete from reviewcomment
where review_serial=#{reviewSerial}
and	  member_email=#{memberEmail}
